mapper.select:

  	SELECT *  FROM (
    	SELECT ROWNUM AS RNUM, A.* FROM (
        	select c.category_id ctct,b.board_content,b.board_date,b.board_groupid, b.board_title,b.category_id
				from board b, iclass c
					where board_groupid in (SELECT class_id from iclass )
					and b.board_groupid = c.class_id and b.user_id =#{userId} and b.board_state = 'show' 
					order by board_date asc
             ) A WHERE ROWNUM <= #{criteria.pageNum} * #{criteria.amount}
         ) WHERE RNUM > (#{criteria.pageNum}-1) * #{criteria.amount}
