mapper.select:

		select count(*) from buy, iclass
where buy.class_id=iclass.class_id and iclass.user_id=#{userId}
order by buy.buy_date desc


				SELECT *
FROM
(
    SELECT ROWNUM rn, c.*
    FROM
    (
        SELECT buy_date, class_title, buy_realpay+buy_point buyProfit
        FROM buy, iclass
        where buy.class_id=iclass.class_id and iclass.user_id = #{userId}
        order by buy.buy_date desc
    ) C
     WHERE rownum <= #{cri.pageNum} * #{cri.amount}
)
WHERE rn > (#{cri.pageNum}-1) * #{cri.amount}


SELECT *
FROM
(
    SELECT ROWNUM rn, bc.*
    FROM
    (
        select class_id, class_title, class_state, class_joinmember
		from iclass
		where user_id = #{userId}
    ) bc
     WHERE rownum <= #{cri.pageNum} * #{cri.amount}
)
WHERE rn > (#{cri.pageNum}-1) * #{cri.amount}


	select count(*) from 
	(
        select class_id, class_title, class_state, class_joinmember
		from iclass
		where user_id = #{userId}
    )
